import Cal from '.';

const evt: Cal.Event = Cal.createEvent('type', 'name');
evt.addData('foo', 'bar').addData({ foo: 'bar' });
evt.complete(Cal.Status.SUCCESS);

Cal.createEvent('type', 'name', Cal.Status.SUCCESS)
    .addData('foo', 1)
    .complete(Cal.Status.SUCCESS);
Cal.createEvent('type', 'name', Cal.Status.SUCCESS, {
    foo: 'bar'
});

const hb: Cal.Event = Cal.createHeartbeat('type', 'name');
hb.complete(Cal.Status.SUCCESS);

Cal.createHeartbeat('type', 'name', Cal.Status.SUCCESS);
Cal.createHeartbeat('type', 'name', Cal.Status.SUCCESS, {
    foo: 'bar'
});

const tx: Cal.Transaction = Cal.createTransaction('type', 'name');
tx.complete(Cal.Status.SUCCESS);

Cal.createTransaction('type', 'name', (err, tx) => {
    tx.addData('foo', true);
    tx.complete(Cal.Status.SUCCESS);
});
