'use strict';
const { assert } = require('chai');
const { openai } = require('../../../');
const AppContext = require('@ebay/app-context-ebay');
AppContext.appName = 'usvbff';

describe('openai', () => {

    describe('openai.createImage', () => {
        it('should be a function', () => {
            assert.equal(typeof openai.createImage, 'function');
        });

        it('should create a image', (done) => {
            openai.createImage({
                prompt: "a multi-colored umbrella on the beach, disposable camera",
                size: "1024x1024",
                n: 1
            }).then(res => {
                assert.hasAllKeys(res, [
                    'id',
                    'status',
                    'result'
                ]);
                done();
            }).catch(err => {
                done(err);
            });
        }, 40000);
    });
});