/**
 * @author <a href="tkimmet@ebay.com">Tim Kimmet</a>
 *
 * Currency is an eBay type that encapsulates the 4217 ISO 3-letter code and some other
 * attributes about a Currency.
 * <p>
 * In addition, Currency maps the ISO 3-letter code to the eBay currency stored in
 * table/field EBAY_CURRENCIES.ID
 * <p>
 * Other utility methods are provided to access the max amount for that currency, etc.
 * <p>
 * The actual Currency instances are code-generated. Search for CurrencyCodeGenerator
 * to see the Code Generator. It makes use of a Excel spreadsheet to code-generate
 * the actual Currency instances in this class.
 */

    /**
     * 3-letter 4217 ISO Currency code
     */
    // this.iso4217Code;

    /**
     * 3-digit ISO Currency code
     */
    // this.iso4217Digit;

    /**
     * 0-filled 3-digit ISO Currency code as String
     */
    // this.iso4217DigitStr;

    /**
     * the symbol (e.g. '$') in its home locale
     * This comes from a resource table and not from the database
     */
    // this.symbol;

    /**
     * the number of decimals places
     */
    // this.decimals;

    /**
     * true if the currency symbol is
     * 		a prefix in its home locale; false otherwise
     */
    // this.prefix;

    /**
     * Table EBAY_CURRENCIES, Field ID
     */
    // this.eBayCurrencyId;

    /**
     * the maximum amount that this currency should achieve
     */
    // this.maxAmount;


class Currency {
    /**
     * Construct a Currency object. Private constructor - no Currency objects
     * should be created other than those defined in this Enum class.
     *
     * @param iso4217Code 3-letter 4217 ISO Currency code
     * @param iso4217Digit 3-digit 4217 ISO Currency code
     * @param symbol the symbol (e.g. '$') in its home locale
     * @param decimals the number of decimals places
     * @param prefix true if the currency symbol is
     * 		a prefix in its home locale; false otherwise
     * @param eBayCurrencyId true if the currency symbol
     * 			is a prefix in its home	locale
     * @param maxAmount the maximum amount that this currency should achieve
     */
    constructor(iso4217Code, iso4217Digit, symbol, decimals,
    	prefix, eBayCurrencyId, maxAmount) {

        this.code = iso4217Code;
        this.iso4217DigitCode = iso4217Digit;
        this.symbol = symbol;
        this.decimals = decimals;
        this.prefix = prefix;

        const unpadded = '000' + iso4217Digit;
        this.iso4217DigitCodeStr = unpadded.substring(unpadded.length - 3);

        this.currencyId = eBayCurrencyId;
        this.maxAmount = maxAmount;
        // register currency
        addCurrency(this);
    }

    isSymbolPrefix() {
        return this.prefix;
    }

    getCode() {
        return this.code;
    }

    getIso4217DigitCode() {
        return this.iso4217DigitCode;
    }

    getIso4217DigitCodeString() {
        return this.iso4217DigitCodeStr;
    }

    getSymbol() {
        return this.symbol;
    }

    getCurrencyId() {
        return this.currencyId;
    }

    getDecimals() {
        return this.decimals;
    }

    getMaxAmount() {
        return this.maxAmount;
    }
}

