/**
 * Internal Utility Method:
 *
 * iso4217codeMap: Cross-Reference Mapping from ISO 4217 Currency Code to the Currency instance
 * ebayCurrencyIdMap: Cross-Reference Mapping from eBay CurrencyID to the Currency instance
 *
 * Used by findCurrency(String theCode) below...
 */

function addCurrency(currency) {
    if (iso4217codeMap[currency.code]){
        throw new Error("Critical Error: more than one Currency Object " +
            "is defined with the same ISO 4217 Code \"" + currency.code + "\"");
    }

    if (currencyArray[currency.currencyId]) {
        throw new Error("Critical Error: more than one Currency Object " +
            "is defined with the same Currency ID \"" + currency.currencyId + "\"");
    }

    iso4217codeMap[currency.code] = currency;
    currencyArray[currency.currencyId] = currency;
    isoByDigitalCode[currency.iso4217DigitCode] = currency;
}

module.exports.Currencies = CURRENCY_ENUM;

module.exports.addCurrency = /^test$/.test(process.env.NODE_ENV || '') ?
    addCurrency : undefined;

/**
 * Find a currency by the 3-digit ISO 4217 code
 * Or
 * Find a currency, given a valid eBay Currency ID (EBAY_CURRENCY.ID)
 *
 * @return    the currency, or null if not found
 */
module.exports.findCurrency = function (theCode) {
    if (typeof theCode === 'string') {
        return iso4217codeMap[theCode];
    }

    if (theCode < 0 || theCode >= currencyArray.length) {
        return null;
    }
    return currencyArray[theCode];
};

/**
 * Find a Currency, given the ISO 4217 code as an integer
 *
 * @return Currency that maps to the ISO 4217 code; or null if one is not found
 */
