'use strict';

var cal = require('@ebay/cal');

function createEvent(type, name, status, data) {
    cal.createEvent(type, name, status, data).complete();
}

module.exports = {
    createEvent: createEvent,
    createErrorEvent: function (data) {
        data = data instanceof Error && data.stack || data;
        this.createEvent('Error', 'Cookies', 2, data);
    },
    createWarnEvent: function (data) {
        data = data instanceof Error && data.stack || data;
        this.createEvent('Warning', 'Cookies', 4, data);
    },
    createInfoEvent: function (data) {
        data = data instanceof Error && data.stack || data;
        this.createEvent('Info', 'Cookies', 0, data);
    }
};