import { Request, Response } from 'express';

export interface CookiesContext {
    getCookieValue(name: string): string;
    setCookieValue(name: string, value: string): void;
    removeCookie(name: string): void;
    hasFlag(name: string): boolean;
    setFlag(name: string, value: boolean): void;
    getCookieBitNameFromId(id: number): string;
}

export function middleware(options?: any): (req: Request, res: Response, next: any) => any;

declare module '@ebay/commons-ebay' {
    interface EbayContext {
        cookies: CookiesContext;
    }
}
