/// <reference types="@ebay/commons-ebay" />
import { middleware } from "@ebay/cookies-ebay";
import { Request, Response } from 'express';

middleware();

(req: Request, res: Response) => {
    req.ebay.cookies.getCookieValue('foo');
    req.ebay.cookies.setCookieValue('foo', "bar");
    req.ebay.cookies.removeCookie('foo');
    req.ebay.cookies.hasFlag('foo');
    req.ebay.cookies.setFlag('foo', true);
    req.ebay.cookies.getCookieBitNameFromId(123);
};
