declare interface CommonHeaders {
    authorization: string // All eBay inbound requests should have authorization
}

declare interface HTTPRequest {
    headers?: CommonHeaders
}

declare interface eBay {
    getUserId(): string
    getAccountId(): string
    //TODO add the other types for ebay request object
}

declare interface eBayRequest extends HTTPRequest {
    ebay?: eBay
    params?: any
    query?: any
    body?: any | string
    secrets?: FideliusSecrets
    method?: string
    path?: string
    ip?: string
}

declare interface eBayRequestContext extends eBayRequest {

}

declare interface ServiceRequest {
    body?: object | string
    headers?: object
    params?: object
    query?: object
    path?: string
    options?: object
    method: string
}

declare interface ServiceRequestOptionalCallback {
    (resolve: Function, reject: Function, error: object, response: ServiceResponse): void
}
