'use strict';
var WebContext = require('./WebContext');
var ServiceContext = require('./ServiceContext');

module.exports.create = function create(req, res, config) {
    req.ebay = req.ebay || {};
    var ep = req.ebay.experimentation = req.ebay.isService ?
        new ServiceContext(req, res, config) :
        new WebContext(req, res, config);

    return ep;
};
