interface Headers {
    [key: string]: string;
}

interface Callback {
    (error: Error | null, result?: string, body?: any): void;
}

interface RequestOptions {
    method: string;
    body?: string;
}

declare class FideliusClient {
    deleteSecret(path: string, headers: Headers, callback: Callback): void;
    setSecret(path: string, value: string, headers: Headers, callback: Callback): void;
    getSecret(path: string, headers: Headers, callback: Callback): void;
    setAclNode(path: string, permissions: unknown, headers: Headers, callback: Callback): void;
    getAclNode(path: string, headers: Headers, callback: Callback): void;

    private _makeRequest(path: string, headers: Headers, options: RequestOptions, callback: Callback, serviceName: string): void;
}

declare const fidelius: FideliusClient;

export default fidelius;
