import {
    parse,
    create
} from '@ebay/header-multi-value-ebay';

const hmv = parse('foo=bar&wsx=baz');
hmv.get('foo'); // $ExpectType string
hmv.set('foo', 'bar'); // $ExpectType HeaderMultiValue
hmv.remove('foo'); // $ExpectType HeaderMultiValue
hmv.names(); // $ExpectType string[]
hmv.toString(); // $ExpectType string

create({foo: 'bar', wsx: 'baz'}); // $ExpectType string
