'use strict';

const localeFormatter = require('@ebay/locale-formatter-ebay');

module.exports = function formatNumber(element, data, context) {
    if (typeof data !== 'number') {
        throw new Error(`value must be number, actual:${data}`);
    }

    const result = localeFormatter.formatNumber(data,
        context.locality && context.locality.locale);

    if (result.error) {
        throw new Error(result.error);
    }
    return result.format;
};
