'use strict';

var assert = require('chai').assert,
    iafClient = require('../'),
    utils = require('./fixtures/utils');

describe(__filename + ': Unit Tests', function () {

    it('should get App Token', function (done) {
        var req = utils.injectRequest();
        req.ebay.appContext.appName = 'nodedemo1';

        iafClient.getAppToken(req, null, function (err, token, meta) {
            assert.ok(token);
            done();
        });

    });

});
