export interface MoneyFormatOptions {
	currencySymbolType?: 'standard' | 'simple' | 'fancy' | 'symbol';
	emitInternalDecimal?: boolean;
	decimal?: number;
}

export interface MoneyFormatResponse {
    format: string;
    currencySymbol: string;
    error?: string | null;
}

export function formatMoney(amount: number, currencyCode: string, locale?: string, options?: MoneyFormatOptions): MoneyFormatResponse;

export interface DateTimeOptions {
    dateTimeOutput?: 'both' | 'timeonly' | 'dateonly';
    dateFormatType?: 'short' | 'medium' | 'numeric' | 'numericnoday' | 'mediumnoday' | 'full' | 'fullcomplete' | 'fullnoyear' | `long`;
    timeFormatType?: 'short' | 'shortampm' | 'shortzone' | 'shortampmzone' | 'medium' | 'long' | 'longampm';
    timeZone?: string;
}

export interface DateTimeResponse {
	dateFormat: string;
	timeFormat: string;
	error?: string | null;
}

export function formatDateTime(date: Date | string | number, locale?: string, options?: DateTimeOptions): DateTimeResponse;

export interface NumberOptions {
    fractionDigits: number;
}

export interface NumberResponse {
    format: string;
    error?: string | null;
}

export function formatNumber(val: number, locale?: string, options?: NumberOptions): NumberResponse;
