import {
    formatMoney,
    formatDateTime,
    formatNumber
} from '@ebay/locale-formatter-ebay';

// $ExpectType MoneyFormatResponse
formatMoney(0.75, 'USD', 'en-US', {
    currencySymbolType: 'standard',
    emitInternalDecimal: true,
    decimal: 2
});

// $ExpectType MoneyFormatResponse
formatMoney(0.75, 'USD');

// $ExpectType DateTimeResponse
formatDateTime(Date.now(), 'en-US', {
    dateTimeOutput: 'both',
    dateFormatType: 'short',
    timeFormatType: 'short'
});

// $ExpectType DateTimeResponse
formatDateTime(new Date(), 'en-US', {
    dateTimeOutput: 'both',
    dateFormatType: 'short',
    timeFormatType: 'short'
});

// $ExpectType DateTimeResponse
formatDateTime('2023-01-11T03:20:11.102Z', 'en-US', {
    dateTimeOutput: 'both',
    dateFormatType: 'short',
    timeFormatType: 'short'
});

// $ExpectType DateTimeResponse
formatDateTime(Date.now());

// $ExpectType NumberResponse
formatNumber(2000, 'en_US', {
    fractionDigits: 2
});

// $ExpectType NumberResponse
formatNumber(2000);
