// Internal type definitions for @ebay/logging-inc 4.0
// Project: https://github.corp.ebay.com/nodejs/logging-inc/
// Definitions by: Timur Manyanov <https://github.corp.ebay.com/tmanyanov>
// Definitions: https://github.com/DefinitelyTyped/DefinitelyTyped

import Cal from '@ebay/cal';

export interface LogTransaction {
    end(): Cal.Transaction;
    addData(key: string, data: any): this;
    addData(data: any): this;
    fail(error: Error, status?: Cal.Status, data?: any): Cal.Transaction;
    fail(message: string, error: Error, status?: Cal.Status, data?: any): Cal.Transaction;
}

export interface Logger {
    log(...args: any[]): any;
    debug(...args: any[]): any;
    info(...args: any[]): any;
    warn(...args: any[]): any;
    error(...args: any[]): any;
    fatal(...args: any[]): any;
    isLogLevelEnabled(loglevel: string): boolean;
    profile(obj: any, method: any, loglevel?: string): any;
    type(type: string): Logger;
    begin(type: string, name: string, callback: (tx: LogTransaction) => void): void;
    begin(type: string, name: string): LogTransaction;
}

export const LOG_LEVELS: string[];

export function configure(merge: any): void;

export function getLogger(options: any): Logger;

export function logger(options: any): Logger;

export function setupLogLevels(name: any): any;
