import { EPTag } from './EpTagsEventMap';

type EvaluationResults = {
    evaluationResults: FlagEvalDetail[];
    epTagsEvent: string;
};

type EvaluationResultsMap = {
    evaluationResultsMap: Map<string, FlagEvalDetail>;
    epTagsEvent: string;
}

type FlagEvalDetail = {
    featureFlagKey: string;
    featureFlagValue: any;
    details: Details;
    isClientSide: boolean;
    isPureFeatureFlag?: boolean;
    isPoolMatched?: boolean;
};

type Details = {
    variantName: string;
    variantId: number;
    reason: string;
    errorCode: string;
    variantDataType?: string;
    deferredForRuleLevel?: boolean;
    deferredForEpAaffLevel?: boolean;
};

type ClientSideEvaluationResults = {
    clientSideEps: FlagEvalDetail[];
    qTags: EPTag[];
    siteId: number;
};

export {
    EvaluationResults,
    EvaluationResultsMap,
    FlagEvalDetail,
    Details,
    ClientSideEvaluationResults,
};