import { Tracer, TracerProvider } from '@opentelemetry/api';

/**
 * Tracer name
 */
export const name: string;

/**
 * Tracer version
 */
export const version: string;

export function configure(config: any): Promise<TracerProvider>;
/**
 * Restarts the init cycle for opentelemetry.
 * It is useful when it has been shutdown to re-start it, mostly used in unit tests
 * If it is already running, it will shutdown the current, then configure and start a new one
 */
export function restart(): Promise<TracerProvider>;
/**
 * Shuts down the current opentelemetry manager if it is running.
 * Once shut down, isEnabled will return false;
 */
export function shutdown(): Promise<any>;
/**
 * Returns tracer if opentelemetry is enabled
 */
export function getTracer(): Tracer;
/**
 * Return true if opentelemetry is enabled
 */
export function isEnabled(): boolean;
/**
 * @Returns boolean, true if it is ready or false if it is not.
 *  If nothing goes bad, this function will always return true once
 *  the configuration is loaded and all properties are resolved.
 * @Throws rejection error in case something went wrong
 */
export const isReady: Promise<boolean>;
