import * as tracing from '@ebay/opentelemetry-ebay';
// @ts-ignore
import middleware from '@ebay/opentelemetry-ebay/middleware';

// $ExpectType (req: Request<ParamsDictionary, any, any, ParsedQs, Record<string, any>>, res: Response<any, Record<string, any>>, next: any) => any
middleware();

// $ExpectType Tracer
const tracer = tracing.getTracer();
tracer.startSpan('request');

// $ExpectType boolean
tracing.isEnabled();

// $ExpectType Promise<boolean>
tracing.isReady;

// $ExpectType Promise<TracerProvider>
tracing.configure({ foo: 'bar' });

// $ExpectType Promise<TracerProvider>
tracing.restart();
