'use strict';

const { Metrics } = require('../metrics');
const defaultPercentiles = [0.01, 0.05, 0.5, 0.9, 0.95, 0.99, 0.999];
const defaultBuckets = [0.1, 5, 15, 50, 100, 500];

class ApplicationMetrics extends Metrics {
    createSummary(name, description, labels, percentiles = defaultPercentiles, maxAgeSeconds = 60, ageBuckets = 5) {
        return super.createSummary(name, description, labels, percentiles, maxAgeSeconds, ageBuckets);
    }

    createHistogram(name, description, labels, buckets = defaultBuckets) {
        return super.createHistogram(name, description, labels, buckets);
    }
}

module.exports = new ApplicationMetrics();
