import {expectType} from 'tsd';
import * as security from '@ebay/security-ebay';
import { Request, Response } from 'express';

(req: Request, res: Response) => {
    expectType<boolean>(security.isValidDomain('www.ebay.com'));
    expectType<boolean>(security.isValidRedirect('http://www.ebay.com'));
    expectType<security.KVP>(security.getMaskPayload(req));
    expectType<security.KVP>(security.maskObject({
        foo: 'bar'
    }));
    expectType<boolean>(security.isValidEbayDomain('http://www.ebay.com'));
    expectType<boolean>(security.isSensitiveParameter('pwd'));
}