import * as bootstrap from '@ebay/service-instruments-ebay';
import { Request, Response } from 'express';

const provider = bootstrap.use(__dirname);

async function run(req: Request, res: Response) {
    const client = await provider.createClient('client-id');
    const contextualClient = client.webContext(req);
    const response = await contextualClient.query('query');
    console.log(response.statusCode);
}
