export interface TokenAttributes {
    ScopePolicy?: string;
    ClientType: string;
    AppId: string;
    scope: string;
    appid: string;
    iss: string;
    AppImmutableId: string;
    iat: string;
    client_id: string;
}

export interface TokenDetails {
    expirationDate: number;
    domain: string;
    userId: string;
    attributes: TokenAttributes;
}

export interface EbaySecurityContext {
    getTokenAttributes(): TokenAttributes;
    getTokenDetails(): TokenDetails;
}

declare module 'express-serve-static-core' {
    interface Request {
        security: EbaySecurityContext;
    }
}