/// <reference types="." />

import { Request, Response } from 'express';

(req: Request, res: Response) => {
    req.security.getTokenAttributes().ScopePolicy;
    req.security.getTokenDetails().expirationDate;
    req.security.getTokenDetails().attributes.ScopePolicy;
    req.security.getTokenDetails().attributes.ClientType;
    req.security.getTokenDetails().attributes.AppId;
    req.security.getTokenDetails().attributes.scope;
    req.security.getTokenDetails().attributes.appid;
    req.security.getTokenDetails().attributes.iss;
    req.security.getTokenDetails().attributes.AppImmutableId;
    req.security.getTokenDetails().attributes.iat;
    req.security.getTokenDetails().attributes.client_id;
};
