import {
    EbaySiteNameEnum,
    EbaySiteIdEnum
} from '@ebay/site-meta-resources-ebay';

export * from '@ebay/site-meta-resources-ebay';

export interface SiteMeta {
    readonly id: EbaySiteNameEnum;
    readonly value: EbaySiteIdEnum;
    readonly iso: number;
    readonly currency: string;
    readonly locale: string;
    readonly area: string;
    readonly description: string;
    readonly roverRotationId: string;
    readonly partner: string[];
}

export function getSiteByName(name: string): SiteMeta;

export function getCountries(): EbaySiteIdEnum;

export interface CurrencyMeta {
    code: string;
    decimals: number;
    name: string;
    number: string;
    symbol: string;
}

export function getCurrencies(): CurrencyMeta;

export interface CountryMeta {
    code: string;
    token: string;
    text: string;
    translatedtext: string;
}

export function getCountriesBySite(site: SiteMeta): CountryMeta;

export function getNationalitiesBySite(site: SiteMeta): CountryMeta;

export function getSiteById(siteId: EbaySiteIdEnum): SiteMeta;

export interface StateMeta {
    code: string;
    token: string;
    text: string;
    translatedtext: string;
}

export function getStatesBySite(site: SiteMeta): StateMeta[] | null | undefined;

export function getStatesBySiteId(siteId: number): StateMeta[] | null | undefined;

export function getStatesListByName(name: string): StateMeta[] | null | undefined;
