import * as Assert from "assert";
import {
    EbaySiteNameEnum, EbaySiteIdEnum,
} from "../dist/index";

describe(__filename, () => {
    it("EbaySiteNameEnum", () => {
        Assert.strictEqual("EBAY_US", EbaySiteNameEnum.EBAY_US);
        Assert.strictEqual(EbaySiteNameEnum.EBAY_US, EbaySiteNameEnum.EBAY_US);
        Assert.strictEqual("EBAY_US", EbaySiteNameEnum[EbaySiteNameEnum.EBAY_US]);

        Assert.strictEqual("EBAY_DE", EbaySiteNameEnum.EBAY_DE);
        Assert.strictEqual("EBAY_DE", EbaySiteNameEnum.EBAY_DE);
        Assert.strictEqual("EBAY_DE", EbaySiteNameEnum[EbaySiteNameEnum.EBAY_DE]);
        Assert.strictEqual("EBAY_PF", EbaySiteNameEnum[EbaySiteNameEnum.EBAY_PF]);
    });

    it("EbaySiteIdEnum", () => {
        Assert.strictEqual(0, EbaySiteIdEnum.EBAY_MAIN);
        Assert.strictEqual("EBAY_MAIN", EbaySiteIdEnum[0]);
        Assert.strictEqual("EBAY_MAIN", EbaySiteIdEnum["0"]);

        Assert.strictEqual(1, EbaySiteIdEnum.EBAY_US);
        Assert.strictEqual("EBAY_US", EbaySiteIdEnum[1]);
        Assert.strictEqual("EBAY_US", EbaySiteIdEnum["1"]);

        Assert.strictEqual(77, EbaySiteIdEnum.EBAY_DE);
        Assert.strictEqual("EBAY_DE", EbaySiteIdEnum[77]);
        Assert.strictEqual("EBAY_DE", EbaySiteIdEnum["77"]);
    });
});
