'use strict';

const Provider = require('@ebay/service-client-ebay');

module.exports = {
    getConfig(appName) {
        return new Promise((resolve, reject) => {
            Provider.context({}).getClient('spellcaster-client-ebay')
                .get()
                .path(`${appName}/config`)
                .end((err, res) => {
                    if (err) {
                        return reject(err);
                    }
                    resolve(res.body);
                });
        });
    }
};
