'use strict';

var EbayModule = function (id, name) {
    this.name = name;
    this.id = id;

};
EbayModule.prototype.toString = function () {
    return this.id;
};
EbayModule.prototype.equals = function (x) {
    if (!x || !x.id) {
        return false;
    }
    return (x.id === this.id);
};

var EbayModules = function () {
};

EbayModules.prototype.newModule = function (id, name) {
    if (this[id]) {
        return this[id];
    }
    var m = this[id] = new EbayModule(id, name);
    return m;

};

EbayModules.prototype.getModuleById = function (id) {
    return this[id];
};
var _instance = new EbayModules();

module.exports = _instance;
