'use strict';

/**
 * This is a rewrite to com.ebay.kernel.presentation.sojourner.SojournerEventEnum
 */
var EVENT_MAP = {};
var SojournerEventEnum = function (name, value) {
    this.name = name;
    this.value = value;
    EVENT_MAP[name] = this;
};
SojournerEventEnum.prototype.toString = function () {
    return this.value;
};
module.exports = {
    COOKIE: new SojournerEventEnum('c', 1),
    APPLICATION: new SojournerEventEnum('a', 2),
    CLICK: new SojournerEventEnum('k', 3),
    getEventByName: function (name) {
        return EVENT_MAP[name];
    }
};
