'use strict';

/**
 * This is a rewrite to com.ebay.kernel.presentation.sojourner.SojournerEventEnum
 */
var EVENT_MAP = {};
var VectorTypeEnum = function (type, prefix) {
    this.type = type;
    this.prefix = prefix;
    EVENT_MAP[type] = this;
};
VectorTypeEnum.prototype.toString = function () {
    return this.type;
};
module.exports = {
    NUMBER: new VectorTypeEnum(0, '!_'),
    STRING: new VectorTypeEnum(1, '!'),
    getType: function (type) {
        return EVENT_MAP[type];
    }
};
