'use strict';

var ServiceContext = require('./service-context');
var WebContext = require('./web-context');

module.exports.getFactory = function getFactory(type) {
    return {
        create: function create(req, res) {
            if (type === 'service') {
                return new ServiceContext(req, res);
            }

            return new WebContext(req, res);
        }
    };
};
