'use strict';

// source of truth is in production
var oldEnv = process.env.NODE_ENV;
process.env.NODE_ENV='postinstall';

var preloader = require('../lib/tools/preloader');

preloader(function (err) {
    if (oldEnv) {
        process.env.NODE_ENV = oldEnv;
    }
    else {
        delete process.env.NODE_ENV;
    }

    process.exit(err ? 1 : 0);

});
