'use strict';

// this module is for backward compatibility for conversion from cubejs
function HelperNode(props) {
    HelperNode.$super.call(this, 'tracking-helper');
    if (props) {
        this.setProperties(props);
    }
}

HelperNode.nodeType = 'element';

HelperNode.prototype = {
    doGenerateCode: function (template) {
        throw new Error('The tracking-helper node should not remain in the tree');
    }
};

module.exports = HelperNode;
