'use strict';
var helperPath = require.resolve('./tracking-helper');

module.exports = function transform(node, compiler, template) {
    var modulePath = compiler.getRequirePath(helperPath);
    var moduleVarName = template.addStaticVar('__trackingHelper', 'require(' + JSON.stringify(modulePath) + ')');
    var varName = node.getAttribute('var');
    node.detach();
    template.addVar(varName, moduleVarName + '.create(out)');
};
