'use strict';
var util = require('./util');

function TrackingHelper(context) {
    this.context = context;
}

TrackingHelper.prototype = {
    spTag: function(moduleId, linkId) {
        return util.spTag(this.context, moduleId, linkId);
    }
};

exports.create = function(context) {
    return new TrackingHelper(context);
};
