/**
 * Curries a function with a variable number of arguments, @see functional.spec.js for usage.
 *
 * @param { function } func function to curry
 * @param  {...any} first   first group of "known" arguments to be passed to func,
 *                          the returning function will have these "curried in".
 */
function curry(func, ...first) {
    return function (...second) {
        return func(...first, ...second);
    };
}

module.exports = {
    curry
};