/**
 * Cleans the provided string of the matched pattern
 *
 * @param {String} string  The string to clean
 * @param {RegExp} pattern The pattern to search for
 * @returns {String}       The cleaned string
 */
function cleanString(string, pattern) {
    if (!(pattern instanceof RegExp)) {
        throw new TypeError('@param: pattern - must be instanceof {RegExp}');
    }

    if (string === null || string === undefined) {
        return string;
    }

    return string.replace(pattern, '');
}

/**
 * Converts strings with underscores to camel case
 *
 * @param {string} initalString     string you'd like to convert
 * @returns {string}                camel cased result
 */
function toCamelCase(initalString) {
    return initalString.replace(/(\_[a-z])/ig, snippet => snippet.toUpperCase().replace('_', ''));
}

module.exports = {
    cleanString,
    toCamelCase
};