function getServicesStatusInformation(callback) {
    let connTesterList = [];
    if (serviceDependencies) {
        connTesterList = serviceDependencies.dependencies.map((service) => {
            return (cb) => {
                connectionTester.test(service.hostname, service.port, 1000, (err, result) => {
                    let serviceConfig = `${service.hostname}:${service.port} (${service.modulename}) (${service.servicename})`;
                    let configName = `nodejs.config.${service.modulename}.${service.servicename}`;
                    cb(null, {
                        'configname': configName,
                        'serviceconfig': serviceConfig,
                        'status': (result.success && 'âˆš Success') || ('X Error: ' + result.error),
                        'color': (result.success ? 4 : 1)
                    });
                });
            };
        });
    }

    async.parallel(connTesterList, (err, connTestResults) => {
        var servicesComponentStatus = {
            'name': 'Services Status Information',
            'alias': 'Services Status Information',
            'status': 'created',
            'properties': _.map(connTestResults, function (val, key) {
                return {
                    'name': val.serviceconfig,
                    'value': val.status,
                    'href': val.configname,
                    'color': val.color
                };
            })
        };
        callback(servicesComponentStatus);
    });
}

function getEnvironmentProperties() {
    var environmentProperties = JSON.parse(process.env);

    var envComponentStatus = {
        'name': 'Environment Properties',
        'alias': 'Environment Properties',
        'status': 'created',
        'properties': _.map(environmentProperties, function (val, key) {
            return {
                'name': key,
                'value': val
            };
        })
    };
    return envComponentStatus;
}


/** Generates Modules Component Status
 * The modules' name & version comes from yarn.lock file
 * The gitLink comes from package.json file from each module under node_modules
 *  Each record will be like this:
 * {
 *    "name": "async",
 *    "version": "2.6.1",
 * }
 * @returns 
 */

function getModules() {
    if (cacheModules && cacheModules.properties && cacheModules.properties.length > 0) {
        return cacheModules;
    }
    let moduleComponentStatus = {
        'name': 'Modules',
        'alias': 'Modules',
        'status': 'created',
        'properties': []
    };

    let yarnLockFile = path.join(process.cwd(), 'yarn.lock');
    let yarnLockFileContent = fs.readFileSync(yarnLockFile, 'utf8');
    let yarnlock = lockfile.parse(yarnLockFileContent);
    let modules = [];
    let module = {};
    for (let key in yarnlock.object) {
        module = {};
        // module name is the key of the object without the version
        module.name = key.substring(0, key.lastIndexOf('@'));
        module.version = yarnlock.object[key].version;
        // Check if the exact same module is already in the array
        // If so, don't add it again
        let found = false;
        for (let i = 0; i < modules.length; i++) {
            if (modules[i].name === module.name && modules[i].version === module.version) {
                found = true;
                break;
            }
        }
        if (!found){
            modules.push(module);
        }
    }

    moduleComponentStatus.properties = _.map(modules, function (val, key) {
        return {
            'name': val.name + '-' + val.version,
            'value': val.version,
        };
    });

    cacheModules = moduleComponentStatus;
    return moduleComponentStatus;
}
